	DNode<ValueT>* _GetNode(size_t index)
	{
		assert(index >= 0 && index < _count);

		size_t pos;
		if (index <= (_count >> 1))
		{
			DNode<ValueT>* pNode = _pHead->next;
			while (pos++ < index)
			{
				pNode = pNode->next;
			}
			return pNode;
